/* ui-slider*/
.ui-slider {
    position: relative;
    text-align: left;
    background: #c5c5c5;
    width: 100%;
    display: inline-block;
    margin-top: 15px;
}
.ui-slider .ui-slider-handle {
    position: absolute;
    z-index: 2;
    width: 15px;
    height: 15px;
    cursor: default;
    -ms-touch-action: none;
    touch-action: none;
    border-radius: 50%;
    outline: none;
}
.ui-slider .ui-slider-range {
    position: absolute;
    z-index: 1;
    font-size: 0;
    display: block;
    border: 0;
    background-position: 0 0;
}
.ui-slider+p{
    margin-top: 15px;
    margin-bottom: 0;
}
.ui-slider+p input,
.ui-slider+p label{
    color: #666666 !important;
    font-size: 14px !important;
    line-height: 30px;
    font-weight: 400 !important;
    margin: 0;
}
.ui-slider+p input[type=text]{
    border:0; 
    color:#f6931f; 
    font-weight:bold;
    max-width: 100px;
}
.ui-slider+p button{
    color: #333333;
    font-size: 14px;
    line-height: 30px;
    margin: 0;
    background: none;
    border: none;
    float: right;
    padding: 0;
}

/* support: IE8 - See #6727 */
.ui-slider.ui-state-disabled .ui-slider-handle,
.ui-slider.ui-state-disabled .ui-slider-range {
    filter: inherit;
}

.ui-slider-horizontal {
    height: 4px;
}
.ui-slider-horizontal .ui-slider-handle {
    top: -5px;
    margin-left: 0;
    background-color: #ff2832;
}
.ui-slider-horizontal .ui-slider-range {
    top: 0;
    height: 100%;
    background: #e5534c;
}
.ui-slider-horizontal .ui-slider-range-min {
    left: 0;
}
.ui-slider-horizontal .ui-slider-range-max {
    right: 0;
}

.ui-slider-vertical {
    width: .8em;
    height: 100px;
}
.ui-slider-vertical .ui-slider-handle {
    left: -.3em;
    margin-left: 0;
    margin-bottom: -.6em;
}
.ui-slider-vertical .ui-slider-range {
    left: 0;
    width: 100%;
}
.ui-slider-vertical .ui-slider-range-min {
    bottom: 0;
}
.ui-slider-vertical .ui-slider-range-max {
    top: 0;
}
